{
    "stationary_time": required [string], // "less_four_hours" | "from_four_to_seven_hours" | "from_eight_to_ten_hours" | "more_than_ten_hours"
    // "During a typical day, how much time do you spend sitting or being mostly inactive (for example, working at a desk, watching TV, driving, or using your phone)?"

    "movement_time": required [string], // "mostly_seated" | "move_a_little" | "move_regularly" | "move_much"
    // "In your daily routines (like at home, work, or running errands), how often do you find yourself moving around — for example, standing, walking short distances, or doing light chores?"

    "limitations": optional [string[]], // ["cardiovascular", "orthopedic", "gastrointestinal"]

    "active_days": required [string], // "never" | "1_2_days" | "3_4_days" | "5_more_days"
    // "How often do you intentionally move your body in a way that raises your breathing or heart rate — such as walking, climbing stairs, household chores, or other physical activities you choose to do?"

    "weight": required [float] // Peso corporeo in kg

    "activities": optional [string[]] // ["brisk_walking", "slow_walking", "light_pilates", "light_toning_gym", "moderate_cycling", "slow_swimming", "moderate_swimming", "moderate_weight_lifting", "light_yoga", "slow_cycling", "light_cross_training", "light_fitness_class"]
    // "Which types of activities from this list sound most enjoyable or realistic for you right now?"
}

{
    "regimen": required [string], // "omnivorous" | "vegetarian"
    "glycemia": required [bool]
    "intestine": required [string], // "none" | "combined" | "constipation" | "gastritis"
    "protein_free": required [bool]
    "lactose_free": required [bool]
    "gluten_free": required [bool]

    "calories_deficit": required [string], // "slowly" | "moderate" | "fast" | "idontknow"
    // "How quickly would you like to lose weight?"
    // - "slowly": -10% calorie deficit (0.5 kg/week)
    // - "moderate": -20% calorie deficit (1 kg/week)
    // - "fast": -25% calorie deficit (1.25 kg/week)
    // - "idontknow": -15% calorie deficit (default moderate approach)

    "sex": required [string], // "male" | "female"
    // User's biological sex for metabolic calculations

    "age": required [int], // Age in years
    // Used for BMR and TDEE calculations

    "weight": required [float], // Weight in kg
    // Current body weight for BMI, BMR, and TDEE calculations

    "height": required [float] // Height in cm
    // Used for BMI calculation and diet personalization
}

